# Dump of table user_legalize_log
# ------------------------------------------------------------

DROP TABLE IF EXISTS `user_legalize_log`;

CREATE TABLE `user_legalize_log` (
  `legalize_id` bigint unsigned NOT NULL AUTO_INCREMENT COMMENT '认证申请ID',
  `user_id` bigint unsigned NOT NULL COMMENT '用户ID',
  `type` tinyint(1) NOT NULL COMMENT '认证类型',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '申请状态',
  `reject_reason` varchar(200) DEFAULT NULL COMMENT '驳回原因',
  `qualification_id` bigint unsigned NOT NULL COMMENT '资质认证材料ID',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`legalize_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='用户认证申请记录';



# Dump of table user_material
# ------------------------------------------------------------

DROP TABLE IF EXISTS `user_material`;

CREATE TABLE `user_material` (
  `material_id` bigint unsigned NOT NULL AUTO_INCREMENT COMMENT '素材id',
  `user_id` bigint unsigned NOT NULL COMMENT '用户ID',
  `type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '素材类型，0-图片, 1-视频, 2-音频',
  `hash` varchar(128) DEFAULT NULL COMMENT '素材指纹',
  `url` varchar(128) NOT NULL COMMENT '素材链接地址',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
